import { Component, Input } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Plan, TreinosService, Workout } from '../treinos.service';

@Component({
  selector: 'app-plan',
  templateUrl: './plan.component.html',
  styleUrls: ['./plan.component.css']
})
export class PlanComponent {
  @Input('plan') plan!: Plan;

  modalVisible = false;
  toggleState: 'create' | 'search' = 'create';
  workoutForm: FormGroup;
  searchResults: any[] = [];

  constructor(private formBuilder: FormBuilder, private service: TreinosService) {
    this.workoutForm = this.formBuilder.group({
      name: ['', Validators.required],
    });
  }

  modalVisibleChange(visible: boolean) {
    this.modalVisible = visible;
  }

  async saveWorkout() {
    if (this.toggleState === 'create' && this.workoutForm.valid) {
      const newWorkout: Workout = { workoutId: "", name: this.workoutForm.value.name, exercises: [] };
      await this.service.addWorkout(newWorkout, this.plan.planId);
      this.plan.workouts.push(newWorkout);
      this.workoutForm.reset();
      this.modalVisible = false;
    }
  }

  searchWorkouts(event: Event) {

    const target = event.target as HTMLInputElement;
    const searchValue = target.value;

    if (this.toggleState === 'search') {
      this.service.searchWorkouts(searchValue).subscribe(results => {
        this.searchResults = results;
      });
    }
  }

  selectWorkout(workout: Workout) {
    // Add the logic to select a workout here.
    // For example, you could add the workout to the workouts array, or do something else with the selected workout:
    console.log('Selected workout:', workout);
    this.modalVisible = false;
  }
}
<div style="margin-top: 30px;margin-bottom: 30px;">
  <h4 style="margin-bottom: 20px;">{{plan.name}}</h4>
  <button class="btn btn-link p-0" (click)="modalVisible = true">+ Add Workout</button>
  <div class="row" style="margin-top: 15px;">
    <app-workout class="col" *ngFor="let workout of plan.workouts" [workout]="workout"></app-workout>
  </div>

  <app-modal [(visible)]="modalVisible"
             (visibleChange)="modalVisibleChange($event)"
             (save)="saveWorkout()"
             title="Add Workout">
    <form [formGroup]="workoutForm">
      <div class="mb-3">
        <div class="btn-group" role="group" aria-label="Create or search">
          <button type="button" class="btn btn-secondary me-2" [class.active]="toggleState === 'create'" (click)="toggleState = 'create'">Create</button>
          <button type="button" class="btn btn-secondary ms-2" [class.active]="toggleState === 'search'" (click)="toggleState = 'search'">Search</button>
        </div>
      </div>

      <ng-container *ngIf="toggleState === 'create'">
        <div class="mb-3">
          <label for="workoutName" class="form-label">Workout Name</label>
          <input type="text" class="form-control" id="workoutName" formControlName="name" required />
        </div>
      </ng-container>

      <ng-container *ngIf="toggleState === 'search'">
        <div class="mb-3">
          <label for="workoutSearch" class="form-label">Search Workouts</label>
          <input type="text" class="form-control" id="workoutSearch" (input)="searchWorkouts($event)" />
        </div>
        <ul class="list-group">
          <li class="list-group-item" *ngFor="let workout of searchResults" (click)="selectWorkout(workout)">
            {{workout.name}}
          </li>
        </ul>
      </ng-container>
    </form>
  </app-modal>
</div>
